SELECT MIN(an1.name) AS actress_pseudonym,
       MIN(t.title) AS japanese_movie_dubbed
  FROM movie_companies AS mc JOIN company_name AS cn ON mc.company_id = cn.id JOIN title AS t ON t.id = mc.movie_id JOIN cast_info AS ci ON ci.movie_id = t.id And  ci.movie_id = mc.movie_id JOIN aka_name AS an1 ON an1.person_id = ci.person_id JOIN role_type AS rt ON ci.role_id = rt.id JOIN name AS n1 ON an1.person_id = n1.id And  n1.id = ci.person_id WHERE ci.note = '(voice: English version)' AND cn.country_code = '[jp]' AND mc.note LIKE '%(Japan)%' AND mc.note NOT LIKE '%(USA)%' AND n1.name LIKE '%Yo%' AND n1.name NOT LIKE '%Yu%' AND rt.role = 'actress';